mapper.update:
		INSERT INTO member (
	    mem_id		    , mem_pw   			 , mem_name
	    , mem_mail      , mem_hp    		 , mem_pro
	    , mem_bir       , mem_gender		 , mem_addr1
    	 , mem_addr2
		) VALUES (
	    #{memId}			  , #{memPw}	 		 , #{memName}
	    , #{memMail}	  , #{memHp}  		 , null
	    , #{memBir} 	  , #{memGender}	 , #{memAddr1}
	    , #{memAddr2}
		)

	UPDATE member	SET
	 	mem_pw = #{memPw}
    , mem_name = #{memName}
    , mem_mail = #{memMail}
    , mem_hp = #{memHp}
    , mem_bir = #{memBir}
    , mem_gender = #{memGender}
    , mem_addr1 = #{memAddr1}
    , mem_addr2 = #{memAddr2}
	WHERE    mem_id = #{memId}


DELETE FROM member
WHERE mem_id = #{memId}
AND mem_pw = #{memPw}
